        if (str) return str.length > n ? str.substr(0, n) + '...' : str
    }

    return (
        <Autocomplete
            multiple
            options={tags.map(t => t.name)}
            freeSolo
            value={selectTags}
            onChange={handleTagsChange}
            sx={{ minWidth: "180px", fontSize: "8px" }}
            renderTags={(value: string[], getTagProps) => {
