    def __init__(self, pin_heights):
        self.pin_heights = pin_heights

    def rotate_pin(self, pin, rotation):
        if rotation == 0:
            return self.pin_heights[pin]
        elif rotation > 0:
            return self.pin_heights[pin] + 1
        else:
            return self.pin_heights[pin] - 1

    def check_pin(self, pin, rotation):
        new_height = self.rotate_pin(pin, rotation)
        if new_height == self.pin_heights[pin]:
            return "green"
        elif new_height > self.pin_heights[pin]:
            return "yellow"
        else:
            return "red"

class SkeletonKey:
    def __init__(self, pin_profile):
        self.pin_profile = pin_profile

    def apply_key(self, lock, pins):
